import { Button, Card, Popover } from "antd";
import {
  EllipsisOutlined,
  HeartOutlined,
  MessageOutlined,
  RetweetOutlined,
} from "@ant-design/icons";
import { useSelector } from "react-redux";
import Proptypes from "prop-types";

const PostCard = ({ post }) => {
  const { me } = useSelector((state) => state.user);
  const id = me && me.id;

  return (
    <>
      <Card
        cover={post.Images[0] && <PostImages images={post.Images}></PostImages>}
        actions={[
          //actionはArrayだからKeyが必要になる
          <RetweetOutlined key="retweet"></RetweetOutlined>,
          <HeartOutlined key="heart"></HeartOutlined>,
          <MessageOutlined key="comment"></MessageOutlined>,
          <Popover
            key="more"
            content={[
              <Button.Group>
                <Button>Update</Button>
                <Button type="danger">Delete</Button>
                <Button>Update</Button>
              </Button.Group>,
            ]}
          >
            <EllipsisOutlined></EllipsisOutlined>
          </Popover>,
        ]}
      ></Card>
    </>
  );
};

PostCard.propTypes = {
  post: Proptypes.shape({
    id: Proptypes.number,
    User: Proptypes.object,
    content: Proptypes.string,
    Images: Proptypes.arrayOf(Proptypes.object),
    Comments: Proptypes.arrayOf(Proptypes.object),
  }).isRequired,
};

export default PostCard;
